import Foundation

struct CategoryContent4 {
    static let promptEngineering = CategoryData(
        id: "prompt_engineering", name: "Prompt Engineering", icon: "text.cursor", colorName: "aiOrange",
        description: "Crafting prompts, system prompts, few-shot, chain-of-thought",
        lessons: [lesson1, lesson2, lesson3, lesson4, lesson5, lesson6], order: 3,
        unlockRequirement: .completeCategory("generative_ai")
    )
    static let lesson1 = LessonData(id: "pe_l1", title: "Introduction to Prompting", description: "The art of talking to AI", categoryId: "prompt_engineering", questions: [
        Question(id: "pe1_q1", type: .multipleChoice, questionText: "A 'prompt' in AI is:", options: ["Instructions you give to an AI model", "The AI's internal code", "A type of programming language", "A hardware component"], correctAnswer: "Instructions you give to an AI model", explanation: "A prompt is the text input you provide to guide the AI's response."),
        Question(id: "pe1_q2", type: .trueFalse, questionText: "The quality of your prompt directly affects the quality of AI output.", options: ["True", "False"], correctAnswer: "True", explanation: "Better prompts lead to more relevant, accurate, and useful AI responses."),
        Question(id: "pe1_q3", type: .fillInBlank, questionText: "Prompt __________ is the skill of crafting effective instructions for AI systems.", options: ["engineering", "deleting", "avoiding", "blocking"], correctAnswer: "engineering", explanation: "Prompt engineering is the emerging discipline of designing prompts that get the best results from AI."),
        Question(id: "pe1_q4", type: .scenarioJudgment, questionText: "Someone types 'help' into ChatGPT and gets a vague response. They blame the AI. Is the AI at fault?", options: ["No, the prompt was too vague", "Yes, AI should read minds", "It depends"], correctAnswer: "No, the prompt was too vague", explanation: "Vague prompts lead to vague answers. Being specific helps AI understand what you actually want."),
        Question(id: "pe1_q5", type: .multipleChoice, questionText: "Which is a better prompt?", options: ["Write a 100-word summary of photosynthesis for a 10-year-old", "Tell me about plants", "Science stuff", "Something interesting"], correctAnswer: "Write a 100-word summary of photosynthesis for a 10-year-old", explanation: "Specific prompts with length, topic, and audience details produce much better outputs."),
        Question(id: "pe1_q6", type: .multipleChoice, questionText: "What makes a good prompt?", options: ["Clear, specific, and provides context", "As short as possible always", "Using technical jargon only", "Being intentionally vague"], correctAnswer: "Clear, specific, and provides context", explanation: "Good prompts clearly communicate what you want, provide relevant context, and specify the desired format.")
    ], order: 0, difficulty: .beginner)
    static let lesson2 = LessonData(id: "pe_l2", title: "Prompt Techniques", description: "Advanced prompting strategies", categoryId: "prompt_engineering", questions: [
        Question(id: "pe2_q1", type: .multipleChoice, questionText: "What is 'few-shot prompting'?", options: ["Including examples in your prompt", "Using a camera with AI", "Asking very short questions", "Sending multiple prompts at once"], correctAnswer: "Including examples in your prompt", explanation: "Few-shot prompting provides the AI with examples of desired input-output pairs to guide its response."),
        Question(id: "pe2_q2", type: .trueFalse, questionText: "Zero-shot means giving the AI no examples before asking it to perform a task.", options: ["True", "False"], correctAnswer: "True", explanation: "Zero-shot prompting asks the AI to perform a task without any examples, relying on its pre-trained knowledge."),
        Question(id: "pe2_q3", type: .matchPairs, questionText: "Match prompting techniques:", matchPairs: [
            MatchPair(term: "Zero-shot", definition: "No examples given"),
            MatchPair(term: "Few-shot", definition: "A few examples provided"),
            MatchPair(term: "Chain-of-thought", definition: "Step-by-step reasoning"),
            MatchPair(term: "Role prompting", definition: "Assign AI a persona")
        ], explanation: "Different prompting techniques are suited for different types of tasks."),
        Question(id: "pe2_q4", type: .fillInBlank, questionText: "__________ prompting asks the AI to explain its reasoning step by step.", options: ["Chain-of-thought", "Zero-shot", "Random", "Silent"], correctAnswer: "Chain-of-thought", explanation: "Chain-of-thought prompting improves reasoning by asking the model to show its work."),
        Question(id: "pe2_q5", type: .multipleChoice, questionText: "When is few-shot prompting most useful?", options: ["When you want output in a specific format", "When you don't care about the output", "When asking yes/no questions", "When the AI already knows the format"], correctAnswer: "When you want output in a specific format", explanation: "Examples are most helpful when you need the AI to follow a specific pattern or format."),
        Question(id: "pe2_q6", type: .scenarioJudgment, questionText: "You need the AI to solve a complex math problem. Should you use chain-of-thought prompting?", options: ["Yes, this is ideal", "No, not helpful", "It depends"], correctAnswer: "Yes, this is ideal", explanation: "Chain-of-thought prompting significantly improves AI performance on reasoning and math tasks.")
    ], order: 1, difficulty: .intermediate)
    static let lesson3 = LessonData(id: "pe_l3", title: "System Prompts & Roles", description: "Setting context for AI", categoryId: "prompt_engineering", questions: [
        Question(id: "pe3_q1", type: .multipleChoice, questionText: "A system prompt is:", options: ["Instructions that set the AI's behavior and context", "An error message from the computer", "The AI's source code", "A type of virus"], correctAnswer: "Instructions that set the AI's behavior and context", explanation: "System prompts define the AI's role, personality, and boundaries before the conversation begins."),
        Question(id: "pe3_q2", type: .trueFalse, questionText: "Telling AI to 'act as a teacher' can improve the quality of explanations.", options: ["True", "False"], correctAnswer: "True", explanation: "Role prompting helps the AI adopt an appropriate tone, vocabulary, and approach for the context."),
        Question(id: "pe3_q3", type: .fillInBlank, questionText: "A __________ prompt defines the AI's role, tone, and constraints before the user starts chatting.", options: ["system", "random", "broken", "empty"], correctAnswer: "system", explanation: "System prompts are set by developers to guide the AI's behavior throughout a conversation."),
        Question(id: "pe3_q4", type: .multipleChoice, questionText: "Which is the best role prompt for getting medical info explained simply?", options: ["You are a friendly doctor explaining to a patient", "You are a computer", "Be yourself", "Respond in code only"], correctAnswer: "You are a friendly doctor explaining to a patient", explanation: "This role prompt gives the AI a specific persona, audience, and communication style."),
        Question(id: "pe3_q5", type: .multipleChoice, questionText: "System prompts are typically set by:", options: ["Application developers", "End users only", "The AI itself", "Hardware manufacturers"], correctAnswer: "Application developers", explanation: "Developers set system prompts to control how the AI behaves within their specific application."),
        Question(id: "pe3_q6", type: .scenarioJudgment, questionText: "A chatbot for children has no system prompt restrictions. Is this a concern?", options: ["Yes, major concern", "No, AI is safe by default", "It depends"], correctAnswer: "Yes, major concern", explanation: "AI chatbots for children need strong system prompts with safety guardrails to prevent inappropriate content.")
    ], order: 2, difficulty: .intermediate)
    static let lesson4 = LessonData(id: "pe_l4", title: "Prompt Iteration", description: "Refining prompts for better results", categoryId: "prompt_engineering", questions: [
        Question(id: "pe4_q1", type: .multipleChoice, questionText: "What is prompt iteration?", options: ["Refining your prompt based on AI responses", "Asking the same question repeatedly", "Giving up after one try", "Using multiple AI models simultaneously"], correctAnswer: "Refining your prompt based on AI responses", explanation: "Prompt iteration means adjusting your prompt based on the AI's output to get progressively better results."),
        Question(id: "pe4_q2", type: .trueFalse, questionText: "The first prompt you write is usually the best one.", options: ["True", "False"], correctAnswer: "False", explanation: "Prompts usually improve through iteration. Your first attempt rarely produces the optimal result."),
        Question(id: "pe4_q3", type: .sortOrder, questionText: "Put prompt iteration steps in order:", correctAnswers: ["Write initial prompt", "Review AI output", "Identify gaps in response", "Refine and re-prompt"], options: ["Identify gaps in response", "Write initial prompt", "Refine and re-prompt", "Review AI output"], explanation: "Iteration follows a cycle of write, review, analyze gaps, and refine."),
        Question(id: "pe4_q4", type: .fillInBlank, questionText: "Adding __________ to your prompt helps the AI understand what you're looking for.", options: ["constraints", "emojis", "passwords", "hashtags"], correctAnswer: "constraints", explanation: "Constraints like word count, format, tone, or audience help narrow the AI's output to what you need."),
        Question(id: "pe4_q5", type: .multipleChoice, questionText: "If the AI's response is too long, what should you add to your prompt?", options: ["A word or character limit", "More questions", "Random words", "Nothing, just accept it"], correctAnswer: "A word or character limit", explanation: "Specifying output length is a simple but effective way to control the AI's response."),
        Question(id: "pe4_q6", type: .multipleChoice, questionText: "Which strategy helps get more creative AI outputs?", options: ["Adjusting the temperature parameter", "Using only formal language", "Asking yes/no questions", "Shortening the prompt to one word"], correctAnswer: "Adjusting the temperature parameter", explanation: "Temperature controls randomness â€” higher values produce more creative, varied responses.")
    ], order: 3, difficulty: .intermediate)
    static let lesson5 = LessonData(id: "pe_l5", title: "Common Prompting Mistakes", description: "What to avoid when prompting", categoryId: "prompt_engineering", questions: [
        Question(id: "pe5_q1", type: .multipleChoice, questionText: "Which is a common prompting mistake?", options: ["Being too vague", "Being too specific", "Providing examples", "Giving context"], correctAnswer: "Being too vague", explanation: "Vague prompts lead to generic, unhelpful responses. Specificity is key to good prompting."),
        Question(id: "pe5_q2", type: .trueFalse, questionText: "Asking multiple unrelated questions in one prompt is a good practice.", options: ["True", "False"], correctAnswer: "False", explanation: "Mixing unrelated questions confuses the AI. It's better to break them into separate, focused prompts."),
        Question(id: "pe5_q3", type: .matchPairs, questionText: "Match mistakes with fixes:", matchPairs: [
            MatchPair(term: "Too vague", definition: "Add specific details"),
            MatchPair(term: "No context", definition: "Explain the situation"),
            MatchPair(term: "No format specified", definition: "Request bullet points or JSON"),
            MatchPair(term: "Ambiguous terms", definition: "Define key terminology")
        ], explanation: "Every common prompting mistake has a straightforward fix."),
        Question(id: "pe5_q4", type: .scenarioJudgment, questionText: "Someone asks AI: 'Tell me everything about science.' Is this a good prompt?", options: ["No, too broad", "Yes, comprehensive", "It depends"], correctAnswer: "No, too broad", explanation: "This prompt is far too broad. The AI cannot cover all of science effectively in one response."),
        Question(id: "pe5_q5", type: .fillInBlank, questionText: "A prompt that assumes the AI has __________ context often leads to poor results.", options: ["missing", "all", "perfect", "unlimited"], correctAnswer: "missing", explanation: "AI doesn't know your specific situation. Always provide relevant context for the best results."),
        Question(id: "pe5_q6", type: .multipleChoice, questionText: "Why should you avoid double negatives in prompts?", options: ["They confuse the AI about what you want", "They make the prompt too short", "They cost more tokens", "They are grammatically perfect"], correctAnswer: "They confuse the AI about what you want", explanation: "Double negatives like 'don't not include' create ambiguity. Use clear, positive statements instead.")
    ], order: 4, difficulty: .intermediate)
    static let lesson6 = LessonData(id: "pe_l6", title: "Real-World Prompting", description: "Practical applications", categoryId: "prompt_engineering", questions: [
        Question(id: "pe6_q1", type: .multipleChoice, questionText: "For summarizing a long article, the best prompt approach is:", options: ["Paste the article and specify summary length and key points", "Just say 'summarize'", "Ask the AI to find the article", "Copy one sentence from it"], correctAnswer: "Paste the article and specify summary length and key points", explanation: "Providing the full text with clear instructions on length and focus areas produces the best summaries."),
        Question(id: "pe6_q2", type: .trueFalse, questionText: "You can use prompts to make AI output structured data like JSON or CSV.", options: ["True", "False"], correctAnswer: "True", explanation: "Specifying output format (JSON, CSV, tables) in your prompt allows AI to produce structured, usable data."),
        Question(id: "pe6_q3", type: .scenarioJudgment, questionText: "A developer asks AI to write code but doesn't specify the programming language. Will the result be reliable?", options: ["No, be specific", "Yes, AI knows best", "It depends"], correctAnswer: "No, be specific", explanation: "Always specify the programming language, framework, and requirements for reliable code generation."),
        Question(id: "pe6_q4", type: .multipleChoice, questionText: "When using AI for creative writing, which prompt element helps most?", options: ["Specifying tone, audience, and style", "Using as few words as possible", "Asking for 'something good'", "Copying someone else's prompt"], correctAnswer: "Specifying tone, audience, and style", explanation: "Clear creative direction including tone (humorous, formal), audience (children, professionals), and style significantly improves creative AI output."),
        Question(id: "pe6_q5", type: .fillInBlank, questionText: "Using AI to check your own writing for errors is called AI-assisted __________.", options: ["editing", "deleting", "encrypting", "compiling"], correctAnswer: "editing", explanation: "AI can help proofread, improve clarity, and suggest better phrasing in your existing writing."),
        Question(id: "pe6_q6", type: .multipleChoice, questionText: "What is the benefit of saving effective prompts?", options: ["You can reuse and adapt them for similar tasks", "They become proprietary AI code", "They make AI faster", "They reduce internet usage"], correctAnswer: "You can reuse and adapt them for similar tasks", explanation: "Building a prompt library saves time and ensures consistent quality across similar tasks.")
    ], order: 5, difficulty: .advanced)
