import Foundation

struct CategoryContent6 {
    static let aiAtWork = CategoryData(
        id: "ai_at_work", name: "AI at Work", icon: "briefcase", colorName: "aiGreen",
        description: "Automation, copilots, AI in hiring, productivity tools, job impact",
        lessons: [lesson1, lesson2, lesson3, lesson4, lesson5, lesson6], order: 5,
        unlockRequirement: .completeCategoryMinimum("how_ai_learns")
    )
    static let lesson1 = LessonData(id: "aw_l1", title: "AI Productivity Tools", description: "Working smarter with AI", categoryId: "ai_at_work", questions: [
        Question(id: "aw1_q1", type: .multipleChoice, questionText: "An AI 'copilot' in the workplace is:", options: ["An AI assistant that helps with tasks", "A robot that flies planes", "A second human worker", "A type of office furniture"], correctAnswer: "An AI assistant that helps with tasks", explanation: "AI copilots assist workers with tasks like writing, coding, analysis, and decision-making."),
        Question(id: "aw1_q2", type: .trueFalse, questionText: "Microsoft Copilot uses AI to help with Word, Excel, and other Office apps.", options: ["True", "False"], correctAnswer: "True", explanation: "Microsoft 365 Copilot integrates AI across Office apps to help with drafting, analysis, and presentations."),
        Question(id: "aw1_q3", type: .matchPairs, questionText: "Match AI tools to their use:", matchPairs: [
            MatchPair(term: "GitHub Copilot", definition: "AI-assisted coding"),
            MatchPair(term: "Grammarly", definition: "AI writing assistance"),
            MatchPair(term: "Otter.ai", definition: "Meeting transcription"),
            MatchPair(term: "Notion AI", definition: "Document & project management")
        ], explanation: "AI productivity tools span across many workplace functions."),
        Question(id: "aw1_q4", type: .fillInBlank, questionText: "AI __________ tools can help workers draft emails, create presentations, and analyze data.", options: ["productivity", "gaming", "social", "entertainment"], correctAnswer: "productivity", explanation: "AI productivity tools are designed to help workers accomplish tasks faster and more effectively."),
        Question(id: "aw1_q5", type: .scenarioJudgment, questionText: "An employee uses AI to write all their reports without review or editing. Is this best practice?", options: ["No, always review AI output", "Yes, AI is always accurate", "It depends"], correctAnswer: "No, always review AI output", explanation: "AI output should always be reviewed and edited by a human to ensure accuracy and appropriateness."),
        Question(id: "aw1_q6", type: .multipleChoice, questionText: "What is the main benefit of AI coding assistants?", options: ["Faster development with auto-suggestions", "They replace all programmers", "They eliminate bugs entirely", "They design user interfaces automatically"], correctAnswer: "Faster development with auto-suggestions", explanation: "AI coding assistants suggest code completions and snippets, accelerating development but not replacing developers.")
    ], order: 0, difficulty: .beginner)
