        Question(id: "aw2_q2", type: .trueFalse, questionText: "AI hiring tools have never been found to exhibit bias.", options: ["True", "False"], correctAnswer: "False", explanation: "Several AI hiring tools have been found to exhibit gender and racial bias based on historical hiring data."),
        Question(id: "aw2_q3", type: .scenarioJudgment, questionText: "A company uses AI to screen resumes and rejects candidates with gaps in employment. Is this ethical?", options: ["No, it's problematic", "Yes, it's efficient", "It depends"], correctAnswer: "No, it's problematic", explanation: "This practice can disproportionately affect caregivers, people with disabilities, and other protected groups."),
        Question(id: "aw2_q4", type: .fillInBlank, questionText: "AI hiring tools should be __________ regularly to ensure they don't discriminate.", options: ["audited", "ignored", "marketed", "sped up"], correctAnswer: "audited", explanation: "Regular audits help identify and correct biases in AI hiring systems."),
        Question(id: "aw2_q5", type: .multipleChoice, questionText: "New York City requires companies to:", options: ["Audit AI hiring tools for bias before use", "Use only AI for all hiring", "Avoid all technology in hiring", "Share all employee data publicly"], correctAnswer: "Audit AI hiring tools for bias before use", explanation: "NYC's Local Law 144 requires companies to conduct bias audits on AI hiring tools and notify candidates."),
        Question(id: "aw2_q6", type: .multipleChoice, questionText: "Which is a legitimate concern about AI video interview analysis?", options: ["It may penalize accents or disabilities", "It makes interviews too easy", "It's too slow", "It costs nothing"], correctAnswer: "It may penalize accents or disabilities", explanation: "AI video analysis can inadvertently penalize candidates for accents, facial differences, or disabilities.")
    ], order: 1, difficulty: .intermediate)
    static let lesson3 = LessonData(id: "aw_l3", title: "Automation & Jobs", description: "Will AI take your job?", categoryId: "ai_at_work", questions: [
