        Question(id: "ah2_q2", type: .trueFalse, questionText: "Traditional drug development usually takes 10-15 years. AI could significantly reduce this.", options: ["True", "False"], correctAnswer: "True", explanation: "AI can accelerate early drug discovery by years, though clinical trials still require time."),
        Question(id: "ah2_q3", type: .fillInBlank, questionText: "AlphaFold by DeepMind solved the protein __________ problem, a major breakthrough for biology.", options: ["folding", "cooking", "counting", "naming"], correctAnswer: "folding", explanation: "AlphaFold predicts protein 3D structures from amino acid sequences, solving a 50-year-old grand challenge in biology."),
        Question(id: "ah2_q4", type: .multipleChoice, questionText: "What AI technique is most useful in analyzing molecular structures?", options: ["Deep learning on molecular graphs", "Simple keyword search", "Random selection", "Social media analysis"], correctAnswer: "Deep learning on molecular graphs", explanation: "Deep learning can analyze complex molecular structures to predict properties and interactions."),
        Question(id: "ah2_q5", type: .scenarioJudgment, questionText: "An AI suggests a new drug compound. Should it go directly to patients?", options: ["No, clinical trials are still required", "Yes, if the AI is confident", "It depends"], correctAnswer: "No, clinical trials are still required", explanation: "AI predictions must still be validated through rigorous clinical trials to ensure safety and efficacy."),
        Question(id: "ah2_q6", type: .multipleChoice, questionText: "What is 'drug repurposing' with AI?", options: ["Finding new uses for existing approved drugs", "Recycling old medications", "Renaming drugs", "Making drugs cheaper"], correctAnswer: "Finding new uses for existing approved drugs", explanation: "AI can identify potential new therapeutic uses for already-approved drugs, speeding up treatment development.")
    ], order: 1, difficulty: .intermediate)
