import Foundation

struct CategoryContent8 {
    static let aiCreativeArts = CategoryData(
        id: "ai_creative_arts", name: "AI in Creative Arts", icon: "paintpalette", colorName: "aiIndigo",
        description: "Image generation, music AI, writing assistants, copyright debates",
        lessons: [lesson1, lesson2, lesson3, lesson4, lesson5, lesson6], order: 7,
        unlockRequirement: .completeCategoryMinimum("how_ai_learns")
    )
    static let lesson1 = LessonData(id: "aca_l1", title: "AI Visual Art", description: "When machines create art", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca1_q1", type: .multipleChoice, questionText: "AI-generated art is created by:", options: ["Algorithms trained on existing artwork", "Robots holding paintbrushes", "Scanning your thoughts", "Downloading from a secret database"], correctAnswer: "Algorithms trained on existing artwork", explanation: "AI art generators learn patterns from millions of existing images, then create new ones."),
        Question(id: "aca1_q2", type: .trueFalse, questionText: "An AI-generated artwork once won a state fair art competition.", options: ["True", "False"], correctAnswer: "True", explanation: "In 2022, an AI-generated image won first place at the Colorado State Fair, sparking debate about AI art."),
        Question(id: "aca1_q3", type: .scenarioJudgment, questionText: "An artist uses AI to generate a starting image, then spends hours refining and painting over it. Is this 'real' art?", options: ["Yes, it's a creative process", "No, AI did the work", "It depends"], correctAnswer: "Yes, it's a creative process", explanation: "Using AI as a tool in the creative process, combined with human creativity, is a valid form of artistic expression."),
        Question(id: "aca1_q4", type: .fillInBlank, questionText: "AI art tools raise questions about __________ ownership of the generated images.", options: ["copyright", "physical", "emotional", "temporal"], correctAnswer: "copyright", explanation: "Who owns AI-generated art is an unresolved legal question in many jurisdictions."),
        Question(id: "aca1_q5", type: .multipleChoice, questionText: "What is 'style transfer' in AI art?", options: ["Applying one image's style to another image", "Physically moving paintings between galleries", "Transferring files between computers", "Teaching art students"], correctAnswer: "Applying one image's style to another image", explanation: "Style transfer algorithms can apply the artistic style of one image to the content of another."),
        Question(id: "aca1_q6", type: .multipleChoice, questionText: "What is the main ethical concern with AI art generators?", options: ["They train on artists' work without permission", "They use too much electricity", "They're too expensive", "They only create ugly art"], correctAnswer: "They train on artists' work without permission", explanation: "Many artists are concerned that their work was used to train AI without consent or compensation.")
    ], order: 0, difficulty: .beginner)
    static let lesson2 = LessonData(id: "aca_l2", title: "AI Music", description: "AI composing and producing music", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca2_q1", type: .multipleChoice, questionText: "AI music tools can:", options: ["Generate melodies, harmonies, and full songs", "Physically play instruments", "Replace all musicians", "Only play pre-recorded songs"], correctAnswer: "Generate melodies, harmonies, and full songs", explanation: "AI music tools can compose original music, generate accompaniments, and even produce full songs with vocals."),
        Question(id: "aca2_q2", type: .trueFalse, questionText: "AI can now generate realistic-sounding singing voices.", options: ["True", "False"], correctAnswer: "True", explanation: "AI voice synthesis has advanced to produce remarkably realistic singing voices."),
        Question(id: "aca2_q3", type: .matchPairs, questionText: "Match AI music applications:", matchPairs: [
            MatchPair(term: "Suno", definition: "Full song generation from prompts"),
            MatchPair(term: "AIVA", definition: "AI composer for film scores"),
            MatchPair(term: "Amper", definition: "Background music for content"),
            MatchPair(term: "Endel", definition: "Personalized soundscapes")
        ], explanation: "Various AI tools serve different music creation needs."),
        Question(id: "aca2_q4", type: .fillInBlank, questionText: "AI-generated music raises questions about __________ when it sounds like existing artists.", options: ["copyright", "weather", "geography", "cooking"], correctAnswer: "copyright", explanation: "If AI generates music resembling a specific artist's style, it raises complex copyright and rights questions."),
        Question(id: "aca2_q5", type: .scenarioJudgment, questionText: "A person uses AI to create a song that sounds exactly like a famous singer's voice without permission. Is this okay?", options: ["No, it violates the artist's rights", "Yes, it's just AI", "It depends"], correctAnswer: "No, it violates the artist's rights", explanation: "Using AI to clone an artist's voice without permission raises serious ethical and legal concerns."),
        Question(id: "aca2_q6", type: .multipleChoice, questionText: "How do many musicians feel about AI music tools?", options: ["Opinions are divided — both excited and concerned", "Universally supportive", "Completely against all AI", "They haven't heard of AI"], correctAnswer: "Opinions are divided — both excited and concerned", explanation: "Musicians have mixed feelings — some see AI as a creative tool, others worry about job displacement and rights.")
    ], order: 1, difficulty: .intermediate)
    static let lesson3 = LessonData(id: "aca_l3", title: "AI Writing Assistants", description: "AI helping with creative writing", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca3_q1", type: .multipleChoice, questionText: "AI writing assistants can help with:", options: ["Drafting, editing, brainstorming, and overcoming writer's block", "Only checking spelling", "Only translating languages", "Only writing code"], correctAnswer: "Drafting, editing, brainstorming, and overcoming writer's block", explanation: "AI writing tools assist across the full writing process from ideation to final editing."),
        Question(id: "aca3_q2", type: .trueFalse, questionText: "AI can write a novel that is indistinguishable from human writing.", options: ["True", "False"], correctAnswer: "False", explanation: "While AI can produce impressive text, long-form creative writing still typically lacks the depth and coherence of skilled human authors."),
        Question(id: "aca3_q3", type: .fillInBlank, questionText: "Using AI as a writing __________ preserves human creativity while leveraging AI efficiency.", options: ["collaborator", "replacement", "competitor", "dictator"], correctAnswer: "collaborator", explanation: "The most effective use of AI in writing is as a collaborative tool that enhances rather than replaces human creativity."),
        Question(id: "aca3_q4", type: .multipleChoice, questionText: "What is 'AI-assisted writing' at its best?", options: ["Humans guiding AI output with their creative vision", "AI writing everything with no human input", "Copying text from the internet", "Avoiding AI entirely"], correctAnswer: "Humans guiding AI output with their creative vision", explanation: "The best AI writing comes from a human-AI collaboration where the human provides direction and creativity."),
        Question(id: "aca3_q5", type: .scenarioJudgment, questionText: "An author publishes a novel written 80% by AI and lists only themselves as the author. Is this transparent?", options: ["No, AI contribution should be disclosed", "Yes, they prompted the AI", "It depends"], correctAnswer: "No, AI contribution should be disclosed", explanation: "Significant AI contributions to creative works should be disclosed to readers and publishers."),
        Question(id: "aca3_q6", type: .multipleChoice, questionText: "AI writing detectors:", options: ["Are not always accurate and can produce false positives", "Are 100% reliable", "Can detect all AI writing perfectly", "Are no longer needed"], correctAnswer: "Are not always accurate and can produce false positives", explanation: "AI writing detectors have significant error rates and can incorrectly flag human writing as AI-generated.")
    ], order: 2, difficulty: .intermediate)
    static let lesson4 = LessonData(id: "aca_l4", title: "Copyright & AI Art", description: "Legal battles over AI creativity", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca4_q1", type: .multipleChoice, questionText: "Currently, in the US, AI-generated content with no human creative input:", options: ["Generally cannot be copyrighted", "Gets automatic copyright", "Belongs to the AI company", "Has no legal status at all"], correctAnswer: "Generally cannot be copyrighted", explanation: "US copyright law requires human authorship — purely AI-generated works generally cannot be copyrighted."),
        Question(id: "aca4_q2", type: .trueFalse, questionText: "Multiple lawsuits have been filed by artists against AI art companies.", options: ["True", "False"], correctAnswer: "True", explanation: "Artists have filed class-action lawsuits against companies like Stability AI and Midjourney for using their work without permission."),
        Question(id: "aca4_q3", type: .fillInBlank, questionText: "The concept of '__________ use' is central to the legal debate around AI training data.", options: ["fair", "foul", "free", "full"], correctAnswer: "fair", explanation: "Whether training AI on copyrighted works is 'fair use' is a key legal question being decided in courts."),
        Question(id: "aca4_q4", type: .multipleChoice, questionText: "If you create an image using AI with significant creative input, who likely owns it?", options: ["It's legally unclear and depends on jurisdiction", "The AI company automatically", "Nobody can own it", "The government owns it"], correctAnswer: "It's legally unclear and depends on jurisdiction", explanation: "The copyright status of human-AI collaborative works is still being debated with varying positions by jurisdiction."),
        Question(id: "aca4_q5", type: .scenarioJudgment, questionText: "A game developer uses AI to generate all character art, replacing concept artists. Is this a good practice?", options: ["It's problematic for the creative community", "Yes, it saves money", "It depends"], correctAnswer: "It depends", explanation: "While cost-effective, this raises questions about creative quality, artist employment, and industry impact."),
        Question(id: "aca4_q6", type: .multipleChoice, questionText: "What are some AI companies doing to address copyright concerns?", options: ["Creating opt-out mechanisms and licensing deals", "Ignoring all complaints", "Suing artists back", "Shutting down entirely"], correctAnswer: "Creating opt-out mechanisms and licensing deals", explanation: "Some companies now allow artists to opt out of training datasets and are creating licensing agreements.")
    ], order: 3, difficulty: .advanced)
    static let lesson5 = LessonData(id: "aca_l5", title: "AI in Film & Video", description: "AI revolutionizing visual media", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca5_q1", type: .multipleChoice, questionText: "AI is used in filmmaking for:", options: ["Visual effects, editing, and content generation", "Only watching movies", "Only selling tickets", "Only choosing actors"], correctAnswer: "Visual effects, editing, and content generation", explanation: "AI helps with VFX, de-aging actors, automated editing, script analysis, and even generating video content."),
        Question(id: "aca5_q2", type: .trueFalse, questionText: "AI video generation tools can now create short videos from text prompts.", options: ["True", "False"], correctAnswer: "True", explanation: "Tools like Sora and Runway can generate short video clips from text descriptions."),
        Question(id: "aca5_q3", type: .fillInBlank, questionText: "AI __________ tools can automatically edit raw footage into a polished video.", options: ["editing", "printing", "mailing", "framing"], correctAnswer: "editing", explanation: "AI-powered editing tools can select the best shots, suggest cuts, and automate tedious editing tasks."),
        Question(id: "aca5_q4", type: .multipleChoice, questionText: "The 2023 Hollywood strikes partly addressed:", options: ["AI's role in writing and acting", "Parking lot sizes", "Catering quality", "Office hours"], correctAnswer: "AI's role in writing and acting", explanation: "Both SAG-AFTRA and WGA strikes included provisions about AI use in writing scripts and using actors' likenesses."),
        Question(id: "aca5_q5", type: .scenarioJudgment, questionText: "A studio uses AI to de-age a deceased actor for a new movie without the estate's permission. Is this acceptable?", options: ["No, consent from the estate is needed", "Yes, AI makes it possible", "It depends"], correctAnswer: "No, consent from the estate is needed", explanation: "Using a deceased person's likeness — even digitally — without the estate's consent raises serious ethical and legal issues."),
        Question(id: "aca5_q6", type: .multipleChoice, questionText: "What is 'synthetic media'?", options: ["Content generated or manipulated by AI", "Media stored on synthetic materials", "Fake news articles only", "Science fiction movies"], correctAnswer: "Content generated or manipulated by AI", explanation: "Synthetic media is any content created or modified by AI, including deepfakes, AI video, and generated images.")
    ], order: 4, difficulty: .intermediate)
    static let lesson6 = LessonData(id: "aca_l6", title: "The Future of AI Creativity", description: "Where art and AI are heading", categoryId: "ai_creative_arts", questions: [
        Question(id: "aca6_q1", type: .multipleChoice, questionText: "The most likely future of AI in creative arts is:", options: ["AI as a powerful tool that enhances human creativity", "AI completely replacing all artists", "AI art disappearing entirely", "No change from current state"], correctAnswer: "AI as a powerful tool that enhances human creativity", explanation: "The consensus is that AI will be a creative tool, like the camera or synthesizer, rather than a replacement for human artists."),
        Question(id: "aca6_q2", type: .trueFalse, questionText: "AI will likely create entirely new art forms that don't exist today.", options: ["True", "False"], correctAnswer: "True", explanation: "Just as photography and digital art created new art forms, AI is likely to enable entirely new forms of creative expression."),
        Question(id: "aca6_q3", type: .fillInBlank, questionText: "The intersection of human creativity and AI capability is called __________ creativity.", options: ["augmented", "deleted", "avoided", "reduced"], correctAnswer: "augmented", explanation: "Augmented creativity describes the enhanced creative potential when humans and AI collaborate."),
        Question(id: "aca6_q4", type: .matchPairs, questionText: "Match future AI creative trends:", matchPairs: [
            MatchPair(term: "Interactive stories", definition: "AI adapts narratives to readers"),
            MatchPair(term: "Personalized music", definition: "AI creates songs for individual listeners"),
            MatchPair(term: "Real-time art", definition: "AI generates visuals live"),
            MatchPair(term: "Co-creative tools", definition: "Human and AI create together")
        ], explanation: "These emerging trends show how AI will reshape creative expression."),
        Question(id: "aca6_q5", type: .scenarioJudgment, questionText: "Should art schools teach students how to use AI tools?", options: ["Yes, as one tool among many", "No, keep art pure", "It depends"], correctAnswer: "Yes, as one tool among many", explanation: "Just as art schools teach digital tools, understanding AI as a creative tool prepares students for the future."),
        Question(id: "aca6_q6", type: .multipleChoice, questionText: "What remains uniquely human in creativity?", options: ["Emotional depth, lived experience, and intentionality", "The ability to use a computer", "Speed of creation", "Technical perfection"], correctAnswer: "Emotional depth, lived experience, and intentionality", explanation: "Human creativity draws from lived experience, emotion, and intentional meaning-making that AI cannot replicate.")
    ], order: 5, difficulty: .advanced)
}
