import Foundation

struct CategoryContent9 {
    static let aiHistory = CategoryData(
        id: "ai_history", name: "AI History & Pioneers", icon: "clock.arrow.circlepath", colorName: "aiBrown",
        description: "Turing, McCarthy, AI winters, AlphaGo, ChatGPT moment",
        lessons: [lesson1, lesson2, lesson3, lesson4, lesson5, lesson6], order: 8,
        unlockRequirement: .completeCategoryMinimum("how_ai_learns")
    )
    static let lesson1 = LessonData(id: "aih_l1", title: "The Birth of AI", description: "How it all began", categoryId: "ai_history", questions: [
        Question(id: "aih1_q1", type: .multipleChoice, questionText: "The term 'Artificial Intelligence' was coined in:", options: ["1956 at the Dartmouth Conference", "1984 at a tech expo", "2010 at a Google event", "2022 at an OpenAI launch"], correctAnswer: "1956 at the Dartmouth Conference", explanation: "John McCarthy coined the term at the Dartmouth Summer Research Project on AI in 1956."),
        Question(id: "aih1_q2", type: .trueFalse, questionText: "Alan Turing proposed a test for machine intelligence before the field of AI existed.", options: ["True", "False"], correctAnswer: "True", explanation: "Turing proposed his famous 'Turing Test' in 1950, before AI was formally established as a field in 1956."),
        Question(id: "aih1_q3", type: .fillInBlank, questionText: "The __________ Test asks whether a machine can exhibit intelligent behavior indistinguishable from a human.", options: ["Turing", "Darwin", "Newton", "Einstein"], correctAnswer: "Turing", explanation: "Alan Turing proposed this test in 1950 as a measure of machine intelligence."),
        Question(id: "aih1_q4", type: .sortOrder, questionText: "Order these AI milestones chronologically:", options: ["AI term coined (1956)", "Expert systems era (1980s)", "Turing Test proposed (1950)", "ELIZA chatbot created (1966)"], correctAnswers: ["Turing Test proposed (1950)", "AI term coined (1956)", "ELIZA chatbot created (1966)", "Expert systems era (1980s)"], explanation: "AI development began with theoretical foundations and progressed through practical applications."),
        Question(id: "aih1_q5", type: .multipleChoice, questionText: "Who is considered the 'father of AI'?", options: ["John McCarthy", "Steve Jobs", "Bill Gates", "Mark Zuckerberg"], correctAnswer: "John McCarthy", explanation: "John McCarthy coined the term and organized the Dartmouth Conference that launched the field."),
        Question(id: "aih1_q6", type: .multipleChoice, questionText: "ELIZA, created in 1966, was:", options: ["One of the first chatbots", "A search engine", "A video game", "An operating system"], correctAnswer: "One of the first chatbots", explanation: "ELIZA simulated a Rogerian therapist and was one of the earliest programs to attempt natural language processing.")
    ], order: 0, difficulty: .beginner)
    static let lesson2 = LessonData(id: "aih_l2", title: "AI Winters", description: "When AI fell out of favor", categoryId: "ai_history", questions: [
